/// <reference path="./online-streaming-provider.d.ts" />
/// <reference path="./doc.d.ts"/>

class Provider {
  api = "https://anilibria.top/api/v1";
  baseUrl = "https://anilibria.top";
  headers = { 
    "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36",
    "Referer": "https://anilibria.top/"
  };

  getSettings(): Settings {
    return {
      episodeServers: ["AniLiberty Server"],
      supportsDub: false,
    };
  }

  async search(opts: SearchOptions): Promise<SearchResult[]> {
    const query = opts.query;
    console.log("AniLiberty: Searching for:", query);

    const url = `${this.api}/app/search/releases?query=${encodeURIComponent(query)}`;

    try {
      const response = await fetch(url, {
        headers: this.headers
      });

      if (!response.ok) {
        console.error("AniLiberty: Search request failed:", response.status);
        return [];
      }

      const data = await response.json();

      if (!Array.isArray(data) || data.length === 0) {
        console.log("AniLiberty: No results found");
        return [];
      }

      const results: SearchResult[] = [];

      data.forEach((release: any) => {
        const title = release.name?.main || release.name?.english || "Unknown Title";

        results.push({
          id: release.id.toString(),
          title: title,
          url: "",
          subOrDub: "sub",
          image: release.poster?.src ? `${this.baseUrl}${release.poster.src}` : undefined
        });

        console.log("AniLiberty: Found anime:", title, "ID:", release.id);
      });

      console.log("AniLiberty: Total results:", results.length);
      return results;
    }
    catch (ex: any) {
      console.error("AniLiberty: Search error:", ex);
      return [];
    }
  }

  async findEpisodes(id: string): Promise<EpisodeDetails[]> {
    const url = `${this.api}/anime/releases/${id}`;
    console.log("AniLiberty: Finding episodes for ID:", id);

    try {
      const response = await fetch(url, {
        headers: this.headers
      });

      if (!response.ok) {
        console.error("AniLiberty: Episode request failed:", response.status);
        return [];
      }

      const data = await response.json();

      if (!data.episodes || data.episodes.length === 0) {
        console.log("AniLiberty: No episodes found");
        return [];
      }

      const episodes: EpisodeDetails[] = [];

      data.episodes.forEach((episode: any) => {
        episodes.push({
          id: episode.id,
          number: episode.ordinal || episode.sort_order || 1,
          title: episode.name || `Episode ${episode.ordinal || episode.sort_order || 1}`,
          url: `${this.api}/anime/releases/episodes/${episode.id}`
        });

        console.log("AniLiberty: Found episode:", episode.name || episode.ordinal, "ID:", episode.id);
      });

      // Sort episodes by number
      episodes.sort((a, b) => a.number - b.number);

      console.log("AniLiberty: Total episodes:", episodes.length);
      return episodes;
    }
    catch (ex: any) {
      console.error("AniLiberty: Episode error:", ex);
      return [];
    }
  }

  async findEpisodeServer(episode: EpisodeDetails, _server: string): Promise<EpisodeServer> {
    const server = "AniLiberty Server";
    console.log("AniLiberty: Getting streams for episode:", episode.title, "URL:", episode.url);

    try {
      const response = await fetch(episode.url, {
        headers: this.headers
      });

      if (!response.ok) {
        console.error("AniLiberty: Stream request failed:", response.status);
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();

      console.log("AniLiberty: Episode data keys:", Object.keys(data));

      const videoSources: VideoSource[] = [];

      // Use format like in the documentation example: "1080p - AniLiberty"
      if (data.hls_1080) {
        videoSources.push({
          quality: "1080p - AniLiberty",
          subtitles: [],
          type: "m3u8",
          url: data.hls_1080
        });
        console.log("AniLiberty: Added 1080p stream");
      }

      if (data.hls_720) {
        videoSources.push({
          quality: "720p - AniLiberty",
          subtitles: [],
          type: "m3u8",
          url: data.hls_720
        });
        console.log("AniLiberty: Added 720p stream");
      }

      if (data.hls_480) {
        videoSources.push({
          quality: "480p - AniLiberty",
          subtitles: [],
          type: "m3u8",
          url: data.hls_480
        });
        console.log("AniLiberty: Added 480p stream");
      }

      // Add auto quality as primary option if any stream exists
      if (videoSources.length > 0) {
        const primaryUrl = data.hls_1080 || data.hls_720 || data.hls_480;
        videoSources.unshift({
          quality: "auto",
          subtitles: [],
          type: "m3u8",
          url: primaryUrl
        });
        console.log("AniLiberty: Added auto quality stream as primary");
      }

      if (videoSources.length === 0) {
        console.log("AniLiberty: No video sources found");
        throw new Error("No video sources found");
      }

      const result: EpisodeServer = {
        server: server,
        headers: {
          "Referer": "https://anilibria.top/",
          "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36"
        },
        videoSources: videoSources
      };

      console.log("AniLiberty: Created server with", videoSources.length, "sources");
      console.log("AniLiberty: Video qualities:", videoSources.map(vs => vs.quality).join(", "));
      return result;
    }
    catch (e: any) {
      console.error("AniLiberty: Stream error:", e);
      throw new Error(`Failed to get streams: ${e.message}`);
    }
  }
}
